import React, { useEffect, useState } from 'react'
import "bootstrap/dist/css/bootstrap.min.css";
import './App.css';
import MovieList from './components/MovieList';
import MovieListTitle from './components/MovieListTitle';
import SearchFeature from './components/SearchFeature';


const App = () => {
  const [movies, setMovies] = useState([]);
  const [searchName, setSearchInput] = useState('');

  const getMovieInput = async () => {
    const url = "http://www.omdbapi.com/?s=star wars&apikey=f1ce2576"

    const response = await fetch(url);
    const responseJson = await response.json();

    console.log(responseJson);
    setMovies(responseJson.Search)  
  };

  useEffect(() => {
    getMovieInput();
  }, [searchName]);

  return (
    <div className = 'container-fluid movie-app'>
      <div className = 'col mt-4 mb-4'>
        <MovieListTitle heading='Movies'/>
        <SearchFeature searchName = {searchName} setSearchInput={setSearchInput}/>
      </div>
      <div className = "row">
        <MovieList movies = {movies} />
      </div>
    </div>
  );

};

export default App;
